import React from 'react'
import './Button.css'

export default props => {
    //classes que podem ser usadas
    let classes = 'button '
    classes += props.operation ? 'operation' : ''
    classes += props.operation ? 'operation' : ''
    classes += props.operation ? 'operation' : ''

    return (
        <button 
            className={classes}> 
            {props.label} 
        </button>
    )
}
