import React, {Component} from 'react'

export default class Contador extends Component {    
    maisUm() {
        console.log(this)
    }

    render() {
        return (
            <div>
                <div> Número: {this.props.numero} </div>
                <button onClick={this.maisUm}> Inc</button>
                <button> Dec </button>
            </div>
        )
    }
}

